methods (Access = public)
% initialise new sorting e = updateVars(app, newM, newT, newS)
% update spike assignments from refine updateUnits(app, d, opt)
function r = getBatchRange(app,varargin) c = app.currentBatch; bl = app.t.batchLengths;
if nargin > 1 spec = varargin{1}; spec(spec > length(bl)) = length(bl); spec(spec < 1) = 1; spec = sort(spec); if length(spec) == 1 spec = [spec spec]; end else spec = [c, c]; end r = [sum(bl(1:spec(1)-1)), sum(bl(1:spec(2)))]; if spec(1) ~= 1 r(1) = r(1) - app.m.spikeWidth; end end end
methods (Access = private)
%% data input
% read in data and filter for displaying readFilter(app, b)
%% others
% save current state of GUI and sorting addHistory(app) undoredo(app, dir)
%% unit manipulation
% reassign s.unit_ and s.waves_ structures unitReassigner(app, n, u, w)
%% callbacks
% callback for pressing colourful buttons bottom right UnitButtonPushed(app, src, evt)
%% unit interaction
% update dropdown for units function [] = updateDropdown(app, varargin)
app.LeftUnitDropDown.Items = string(1:length(app.unitArray)); app.RightUnitDropDown.Items = app.LeftUnitDropDown.Items;
if nargin < 2 return; end mode = varargin{1}; current = [string(app.LeftUnitDropDown.Value), string(app.RightUnitDropDown.Value)]; if any(strcmpi(mode, ["add", "remove"])) return; elseif strcmpi(mode, "split") app.RightUnitDropDown.Value = app.RightUnitDropDown.Items(end); elseif strcmpi(mode, "merge") c = app.currentBatch; bl = app.t.batchLengths;
plotBatch = c + [-round(app.PastbatchesField.Value), min([round(app.FuturebatchesField.Value), length(bl)-c])]; if isempty(app.unitArray(str2double(current(1))).getAssignedSpikes(getBatchRange(app,plotBatch))) if current(1) > current(2) app.LeftUnitDropDown.Value = current(2); if str2double(current(2)) > 1 app.RightUnitDropDown.Value = string(str2double(current(2))-1); end elseif current(1) < current(2) app.LeftUnitDropDown.Value = string(str2double(current(2))-1); end end
app.RightUnitDropDown.Value = app.RightUnitDropDown.Value(1); end end
function [] = standardUpdate(app) app.redrawTracePlot(); app.redrawUnitPlots(); app.StatusLabel.Value = "Ready"; app.addHistory(); end
end
% Callbacks that handle component events methods (Access = private)
% Code that executes after component creation function startupFcn(app) app.parseConfig(); app.UIBase.KeyPressFcn = @app.hotkeyPress; setupUserdata(app); setupHotkeys(app);
switchButtons(app, 2);
setupFigures(app);
% initialise sorting variables app.updateDropdown(); app.LeftUnitDropDown.FontColor = getColour(str2double(app.LeftUnitDropDown.Value)); app.RightUnitDropDown.FontColor = getColour(str2double(app.RightUnitDropDown.Value));
app.figureHandles = [app.dataAx, app.leftUnitAx, app.rightUnitAx]; for ii = 1:length(app.figureHandles) hold(app.figureHandles(ii), 'on'); end
app.basicModes = uiToggleGroup([app.SelectedButton, app.PlottedButton, app.BatchButton],... ["selected","plotted","batch"]);
app.ColourGrid.ColumnWidth = repmat({'1x'},[1,25]); app.colourButtons = app.createColourCodes(); [app.colourButtons.Enable] = deal('off');
end
% Menu selected function: InitialiseMenu function InitialiseButtonPushed(app, event) % load dataset and initialise sorting session. See % initialisePopup for more info
app.switchButtons(0); if isfield(app.m,'sRateHz') app.initialisePop = initialisePopup(app,app.m,app.t); else app.initialisePop = initialisePopup(app); end
end
% Menu selected function: LoadMenu function LoadButtonPushed(app, event) % load a previous sorting
app.StatusLabel.Value = "Loading..."; drawnow app.switchButtons(1)
[fN, fP] = uigetfile([app.loadPath '*.mat'],'Pick *.mat file to load'); if fN < 1 app.StatusLabel.Value = "Could not load file!"; return; end cd(fP); figure(app.UIBase);
% check if the loaded file has m, s and t inFile = whos('-file',[fP fN]); if ~any(strcmp('m',{inFile(:).name})) || ~any(strcmp('t',{inFile(:).name})) app.StatusLabel.Value = "The structures 'm', or 't' do not exist!"; return; end if ~any(strcmp('s',{inFile(:).name})) tData = load([fP fN],'m','t','unitArray'); tData.s = tData.unitArray; else tData = load([fP fN],'m','s','t'); end
if app.updateVars(tData.m, tData.t, tData.s) app.StatusLabel.Value = "Load cancelled"; return; end
app.loadPath = fP; app.savePath = fP;
end
% Button pushed function: ImporttemplatesButton function ImporttemplatesMenuSelected(app, event) % import templates from a previous sorting from this/another % dataset and use them to initialise new units in this sorting
for ii = 1:length(app.unitArray) if ~isempty(app.unitArray(ii).loadedTemplateWaves) answer = uiconfirm(app.UIBase,"Templates have already been imported! Load more?","Warning",... 'Icon','warning','DefaultOption',2); if strcmpi(answer, "CANCEL") return; end break; end end
app.StatusLabel.Value = "Loading..."; drawnow
app.switchButtons(1)
% load .mat file of a previous sorting [fN, fP] = uigetfile([app.loadPath '*.mat'],'Pick *.mat file to import as templates'); if fN < 1 app.StatusLabel.Value = "Could not load file!"; return; end
% check if the loaded file has s.clusters inFile = whos('-file',[fP fN]); if any(strcmp('unitArray',{inFile(:).name})) tData = load([fP fN], 'unitArray'); rge = 1:length(tData.unitArray); elseif any(strcmp('s',{inFile(:).name})) tData = load([fP fN], 's'); if ~isfield(tData.s,'clusters') app.StatusLabel.Value = "The structure 's.clusters' does not exist!"; return; end rge = tData.s.clusters; else app.StatusLabel.Value = "The structure 's.clusters' or 'unitArray' does not exist!"; return; end
msg = 'Choose template harvesting location from dataset:'; title = 'Choose template harvesting location'; answer = uiconfirm(app.UIBase,msg,title,... 'Options',{'Start','End','Cancel'},... 'DefaultOption',2,'CancelOption',3);
if strcmpi(answer,'CANCEL') return; end
originName = string([fP, fN]);
for ii = 1:length(rge) if isa(rge, 'double') originUnit = ii; w = tData.unitArray(ii).waves; else originUnit = str2double(tData.s.clusters(ii)); w = tData.s.("waves_"+tData.s.clusters(ii)); end if size(w,1) > app.SpikesusedEditField.Value % put templates into t structure based on harvesting location if strcmpi(answer, 'End') w = w(end-app.SpikesusedEditField.Value+1:end,:,:); elseif strcmpi(answer, 'Start') w = w(1:app.SpikesusedEditField.Value,:,:); end end
app.unitArray = [app.unitArray, unit([],[],app.m.mainCh,... originName,answer,originUnit,w)]; end
app.updateDropdown(); app.LeftUnitDropDown.Value = string(length(app.unitArray)); app.redrawUnitPlots(); app.ShowtemplatesButton.Enable = 1;
app.StatusLabel.Value = "Ready"; app.addHistory();
end
% Menu selected function: SaveMenu function SaveButtonPushed(app, event) % save current sorting
app.StatusLabel.Value = "Saving..."; drawnow
if app.savePath == 0 app.savePath = app.m.fP; end sP = uigetdir(app.savePath,'Select save path');
figure(app.UIBase); if sP == 0 app.StatusLabel.Value = "Ready"; return; end app.savePath = sP; m = app.m; %#ok<ADPROPLC> unitArray = app.unitArray; %#ok<ADPROPLC> t = app.t; %#ok<ADPROPLC> % m.msec = app.m.sRateHz/1000; %#ok<ADPROPLC> % need this for further pipeline stuff - REMOVE
if app.AddtimestampMenu.Checked timeStamp = "_"+datestr(now,'yymmddHHMM'); else timeStamp = ""; end sN = string(app.SavenameEditField.Value)+timeStamp+".mat"; save(app.savePath+string(filesep)+sN,'m', 'unitArray', 't'); app.StatusLabel.Value = "Saved! (" + sN + ")";
end
% Value changed function: LeftUnitDropDown function LeftUnitDropDownValueChanged(app, event) app.redrawUnitPlots(1);
end
% Value changed function: RightUnitDropDown function RightUnitDropDownValueChanged(app, event) app.redrawUnitPlots(2);
end
% Callback function: GotobatchButton, NextbatchButton, % NextbatchMenu, PreviousbatchButton, PreviousbatchMenu function GotobatchButtonPushed(app, event) % move to a chosen batch bl = app.t.batchLengths; c = app.currentBatch;
value = event.Source.UserData; if value == 0 value = round(app.GotobatchField.Value); else value = value + c; end if value == app.currentBatch return; end
if value < 1 value = 1; elseif value > length(bl) value = length(bl); end
app.StatusLabel.Value = "Loading new batch of data..."; drawnow
app.readFilter(value);
standardUpdate(app);
end
% Button pushed function: CleanupButton function CleanupButtonPushed(app, event) % cleans up unit label numbers and removes empty units
if length(app.unitArray) <= 1 return; end
app.StatusLabel.Value = "Cleaning up units..."; drawnow
app.unitArray = app.unitArray.unitRefactorer(); app.unitArray = app.unitArray.unitSorter(); app.updateDropdown();
standardUpdate(app);
end
% Menu selected function: RedoMenu, UndoMenu function UndoMenuSelected(app, event) app.undoredo(event.Source.UserData);
end
% Callback function: NewunitButton, NewunitMenu function NewunitButtonPushed(app, event) % create a new unit
app.StatusLabel.Value = "Adding unit..."; drawnow
app.unitArray = [app.unitArray, unit()]; app.updateDropdown(); app.LeftUnitDropDown.Value = string(length(app.unitArray)); app.LTitle.Value ="";
app.redrawUnitPlots(); app.StatusLabel.Value = "Ready"; app.addHistory();
end
% Button pushed function: AutocreatejunkunitsButton, % AutocreateunitsButton function AutocreateunitsButtonPushed(app, event) % auto-generates units using PCA then deviation index thresholding range = getBatchRange(app); [unassignedSpikes, ~, unassignedInBatch] = app.unitArray.getOrphanSpikes(app.t.rawSpikeSample,range); unassignedWaves = app.rawSpikeWaves(unassignedInBatch,:,:);
if isempty(unassignedSpikes) app.StatusLabel.Value = "No unassigned spikes to put in units in this batch!"; return; end
% ask user for amplitude cutoff point for spikes used in unit generation if event.Source.UserData == 0 prompt = {'Create k units from spikes with peak amplitude MORE -VE than (uV):',... 'k (expected units + a couple)'}; d = 0; else prompt = {'Create k JUNK units from spikes with peak amplitude MORE +VE than (uV):',... 'k (expected units + a couple)'}; d = 1; end answer = inputdlg(prompt, 'Create units', [1 35]);
if isempty(answer) return; end if answer{2} < 0 answer{2} = 3; end
% generate units y = app.xi(app.m.mainCh,unassignedSpikes-range(1)); [ass, candidates] = autoCreateUnits(unassignedWaves, y, app.t.add2UnitThr(2), app.m.sRateHz, ... str2double(answer{1}), str2double(answer{2}), d, app.FuzzyButton.Value,... app.t.templateCropFactor, app.t.deviationSampleWeights);
if isempty(ass) return; end
uAss = unique(ass); uAss(uAss == 0) = []; for ii = uAss I = ass == ii; tempU = unit(unassignedSpikes(candidates(I)), unassignedWaves(candidates(I),:,:)); if event.Source.UserData == 1 tempU.tags = "Junk"; end app.unitArray = [app.unitArray, tempU]; end
app.updateDropdown(); app.LeftUnitDropDown.Value = string(str2double(app.LeftUnitDropDown.Value)+1); standardUpdate(app);
end
% Button pushed function: DeleteunitButton function DeleteunitButtonPushed(app, event) % delete a unit
if length(app.LeftUnitDropDown.Items) == 1 app.StatusLabel.Value = "Cannot delete the last unit"; return; end
app.StatusLabel.Value = "Deleting unit..."; drawnow
value = str2double(app.LeftUnitDropDown.Value); app.unitArray(value) = [];
app.updateDropdown(); app.LeftUnitDropDown.Value = string(1); standardUpdate(app);
end
% Callback function: MergeButton, MergeMenu, % RemovespikeButton, RemovespikeMenu, SplitButton, SplitMenu function basicopsButtonPushed(app, event) % unassign currently selected spikes in left unit value(1) = str2double(app.LeftUnitDropDown.Value); value(2) = str2double(app.RightUnitDropDown.Value);
if isempty(app.unitArray(value(1)).spikeTimes) app.StatusLabel.Value = "Left unit is empty"; return; end
app.StatusLabel.Value = "Performing unit operations..."; drawnow
mode = app.basicModes.getUserData(); if strcmpi(mode, "batch") [~,~,I] = app.unitArray(value(1)).getAssignedSpikes(getBatchRange(app)); elseif strcmpi(mode, "plotted") I = app.plottedWavesIdx; elseif strcmpi(mode, "selected") I = app.figureHandles(2).UserData{1}; % selected spike index along plotted spikes end
[temp, e] = basicUnitOp(app.unitArray, event.Source.UserData{1}, value, I); if isempty(e) app.unitArray = temp; app.updateDropdown(event.Source.UserData{2}); standardUpdate(app); else app.StatusLabel.Value = e; end
end
% Callback function: AddspikeButton, AddspikeMenu, % ForceaddButton, ForceaddMenu function AddspikeButtonPushed(app, event) % pick out spikes manually and add them to a unit either by % deviation matching or by force
value = str2double(app.LeftUnitDropDown.Value);
I = app.figureHandles(1).UserData.selectedUnassigned; if isempty(I) return; end
[unassignedSpikes, ~, unassignedInBatch] = app.unitArray.getOrphanSpikes(app.t.rawSpikeSample,getBatchRange(app)); unassignedWaves = app.rawSpikeWaves(unassignedInBatch,:,:); templateWaves = unassignedWaves(I,:,:);
if event.Source.UserData == 0 % go through deviation matching to add new spikes to unit % add loaded template waves if they exist for this unit if ~isempty(app.unitArray(value).loadedTemplateWaves) templateWaves = [app.unitArray(value).loadedTemplateWaves; templateWaves]; end matches = deviationTemplateMatch(unassignedWaves, templateWaves, app.m.sRateHz, ... app.t.add2UnitThr(2), app.FuzzyButton.Value, app.t.templateCropFactor, app.t.deviationSampleWeights); if ~isempty(matches) app.unitArray = app.unitArray.spikeAdder(value, unassignedSpikes(matches), ... unassignedWaves(matches,:,:)); end elseif event.Source.UserData == 1 % force add spikes to unit - no deviation matching app.unitArray = app.unitArray.spikeAdder(value, unassignedSpikes(I), ... unassignedWaves(I,:,:)); end
standardUpdate(app);
end
% Callback function: AutosplitButton, AutosplitMenu function AutosplitButtonPushed(app, event) % perform PCA on the left unit to find clusters of similar spikes % to make units of. Asks user if they want to keep the new % assignment of spikes or not.
value = str2double(app.LeftUnitDropDown.Value); if isempty(app.unitArray(value).spikeTimes) app.StatusLabel.Value = "Left unit is empty!"; return; end
mode = app.basicModes.getUserData(); if strcmpi(mode, "batch") [~,~,I] = app.unitArray(value).getAssignedSpikes(getBatchRange(app)); elseif strcmpi(mode, "plotted") I = app.plottedWavesIdx; elseif strcmpi(mode, "selected") I = app.figureHandles(2).UserData{1}; % selected spike index along plotted spikes end
if isempty(I) I = 1:length(app.unitArray(value).spikeTimes); end
app.StatusLabel.Value = "Autosplitting left unit into " + string(app.AutosplitField.Value) + " units..."; drawnow
% PCA split unit and check if user confirmed [clust, yn] = unitAutoSplitter(app.unitArray, value, I, app.AutosplitField.Value, [app.yLimLowField.Value, app.yLimHighField.Value]); if ~yn return; end
truncClust = unique(clust); truncClust = truncClust(2:end); unitSpikesBool = false(size(app.unitArray(value).spikeTimes)); unitSpikesBool(I) = true; for ii = truncClust unitSpikesIdx = find(unitSpikesBool); app.unitArray = app.unitArray.unitSplitter(value, unitSpikesIdx(clust==ii)); unitSpikesBool(unitSpikesIdx(clust==ii)) = []; clust(clust == ii) = []; end app.updateDropdown("remove"); standardUpdate(app);
end
% Button pushed function: AutomergeButton function AutomergeButtonPushed(app, event) % perform PCA on the spikes of left unit to find other similar units. % Asks user if they want to keep the new assignment of spikes or not.
value = str2double(app.LeftUnitDropDown.Value); if isempty(app.unitArray(value).spikeTimes) app.StatusLabel.Value = "Left unit is empty!"; return; end
mode = app.basicModes.getUserData(); if strcmpi(mode, "batch") [~,~,I] = app.unitArray(value).getAssignedSpikes(getBatchRange(app)); elseif strcmpi(mode, "plotted") I = app.plottedWavesIdx; elseif strcmpi(mode, "selected") I = app.figureHandles(2).UserData{1}; % selected spike index along plotted spikes end
if isempty(I) I = 1:length(app.unitArray(value).spikeTimes); end
app.StatusLabel.Value = "Automerging left unit into other units..."; drawnow
% PCA split unit and check if user confirmed [clust, yn] = unitAutoMerger(app.unitArray, value, I, [app.yLimLowField.Value, app.yLimHighField.Value]); if ~yn return; end
truncClust = unique(clust); unitSpikesBool = false(size(app.unitArray(value).spikeTimes)); unitSpikesBool(I) = true; for ii = truncClust unitSpikesIdx = find(unitSpikesBool); app.unitArray = app.unitArray.unitMerger([value,ii], unitSpikesIdx(clust==ii)); unitSpikesBool(unitSpikesIdx(clust==ii)) = []; clust(clust == ii) = []; end app.updateDropdown("remove"); standardUpdate(app);
end
% Button pushed function: AutosortButton, % AutosortbatchButton function AutosortButtonPushed(app, event) % sort unassigned spikes in current and future batches into currently initialised units
if length(app.unitArray) == 1 && isempty(app.unitArray(1).spikeTimes) app.StatusLabel.Value = "No units to sort into!"; return; end
if event.Source.UserData == 0 if app.currentBatch > round(app.uptoField.Value) app.StatusLabel.Value = "End batch is before current batch!"; return; end if app.uptoField.Value > length(app.t.batchLengths) batches = app.currentBatch:length(app.t.batchLengths); end app.StatusLabel.Value = "Autosorting up to batchn "+round(app.uptoField.Value); drawnow else batches = app.currentBatch; app.StatusLabel.Value = "Re-sorting batch..."; drawnow end
for ii = batches if event.Source.UserData == 0 app.readFilter(ii); end r = getBatchRange(app); [unassignedSpikes, ~, unassignedInBatch] = app.unitArray.getOrphanSpikes(app.t.rawSpikeSample,r); unassignedWaves = app.rawSpikeWaves(unassignedInBatch,:,:);
% assign spikes to units, if within deviation threshold templateBatches = [-app.PastbatchesTField.Value, app.FuturebatchesTField.Value]; % batches to make templates from r = getBatchRange(app, app.currentBatch+templateBatches);
[devMatrix, ~, thr] = getDevMatrix(app.t.add2UnitThr(1), app.unitArray, unassignedWaves, r, ... app.m.sRateHz, app.SpikesusedEditField.Value, app.FuzzyButton.Value);
[devMins, devMinIdx] = min(devMatrix,[],2);
% update structures for jj = 1:length(app.unitArray) assigned = devMins < thr(jj); assignedUnit = assigned.*devMinIdx; I = assignedUnit == jj; app.unitArray = app.unitArray.spikeAdder(jj, unassignedSpikes(I), ... unassignedWaves(I,:,:)); end end
standardUpdate(app);
end
% Callback function: RefinebatchButton, RefinebatchMenu function RefinebatchButtonPushed(app, event) % refine deviation indices of spikes in current batch to try and assign % them. See refineGUI for more information
% stop user from interacting with main GUI while refining app.switchButtons(0); app.switchButtons(2);
% input variables to refineGUI r = getBatchRange(app);
packet.xi = app.xi; packet.m = app.m; packet.unitArray = app.unitArray; packet.t = app.t; packet.rawSpikeWaves = app.rawSpikeWaves; packet.rawSpikeWavesTimes = app.t.rawSpikeSample(r(1) < app.t.rawSpikeSample & app.t.rawSpikeSample <= r(2)); packet.currentBatch = app.currentBatch; packet.yL = [app.yLimLowField.Value, app.yLimHighField.Value];
packet.numTemplates = app.SpikesusedEditField.Value; packet.templateBatches = [-app.PastbatchesTField.Value, app.FuturebatchesTField.Value]; % batches to make templates from
app.refinePop = refineGUI(app, packet); app.StatusLabel.Value = "Refine window open!";
end
% Button pushed function: OverviewButton function OverviewButtonButtonPushed(app, event) % plot all units' spikes in a popout figure with their 3D PCA components % for easy visualisation. Can choose to not plot junk units
unitsToPlot = 1:length(app.unitArray); junks = app.unitArray.tagcmpi("Junk");
if any(junks) msg = 'Plot which units?'; msgTitle = 'Show Overview'; answer = uiconfirm(app.UIBase,msg,msgTitle,... 'Options',{'All','Exclude junk','Cancel'},... 'DefaultOption',1,'CancelOption',3);
if strcmpi(answer, 'Exclude junk') % remove junk units from the plot unitsToPlot = find(~junks); elseif strcmpi(answer, 'Cancel') return; end end
if isempty(unitsToPlot) app.StatusLabel.Value = "No units to plot!"; return; end
app.StatusLabel.Value = "Plotting unit overview..."; drawnow
if strcmp(app.OverviewDropDown.Value,'Spike') showSpikeOverview(app.unitArray, unitsToPlot, [app.yLimLowField.Value, app.yLimHighField.Value]); elseif strcmp(app.OverviewDropDown.Value,'PCA') showPCAOverview(app.unitArray, unitsToPlot) elseif strcmp(app.OverviewDropDown.Value,'LDA') showLDAOverview(app.unitArray, unitsToPlot) else showDeviationOverview(app, app.unitArray, unitsToPlot) end
app.StatusLabel.Value = "Ready";
end
% Callback function: ScrubthroughunitMenu, UnitscrubButton function UnitscrubButtonPushed(app, event) % scrub through the left unit in time to see which spikes were added % when. Also allows unit splitting and spike deletion at a specific % timepoint in the unit
value = str2double(app.LeftUnitDropDown.Value);
if isempty(app.unitArray(value).spikeTimes) app.StatusLabel.Value = "Left unit is empty!"; return; end
unitScrubber(app,value);
end
% Callback function: UnitfreqButton, UnitfrequencyMenu function UnitfreqButtonPushed(app, event) % plot number of spikes in each unit in each batch
app.StatusLabel.Value = "Plotting unit time series..."; drawnow
bl = length(app.t.batchLengths);
f = figure; ax = axes; set(f, 'Position', [200, 200, 900, 700]);
for ii=1:length(app.unitArray) iiCmap = getColour(ii); ms = getMarker(ii); numInBatch = zeros(1,bl); % number of spikes in each unit in each batch for jj = 1:bl spikesInBatch = app.unitArray(ii).getAssignedSpikes(getBatchRange(app,[jj-1, jj])); numInBatch(jj) = numel(spikesInBatch); % number of spikes in each unit in each batch end line(ax, 1:bl, numInBatch, 'Color', iiCmap,"Marker",ms); end
xlabel(ax, "Batch number"); ylabel(ax, "Frequency"); title(ax, 'Spike frequency for each unit over time') legend(ax, "Unit " + string(1:length(app.unitArray))); app.StatusLabel.Value = "Ready";
end
% Button pushed function: ShowtemplatesButton function ShowtemplatesButtonPushed(app, event) % if templates were imported from another sorting, display them along with % the units generated from them temp = []; for ii = 1:length(app.unitArray) temp = [temp; app.unitArray(ii).loadedTemplateWaves]; end if isempty(temp) app.StatusLabel.Value = "There are no imported templates OR loaded templates are not used!"; return; end
showImportedTemplates(app.unitArray, [app.yLimLowField.Value, app.yLimHighField.Value])
end
% Menu selected function: LZoomMenu, TZoomMenu function ZoomMenuSelected(app, event) if app.interactingFlag(event.Source.UserData) == 2 pan(app.figureHandles(event.Source.UserData),'off'); zoom(app.figureHandles(event.Source.UserData),'on'); app.interactingFlag(event.Source.UserData) = 1; elseif app.interactingFlag(event.Source.UserData) == 1 zoom(app.figureHandles(event.Source.UserData),'off'); app.interactingFlag(event.Source.UserData) = 0; else zoom(app.figureHandles(event.Source.UserData),'on'); app.interactingFlag(event.Source.UserData) = 1; end
end
% Menu selected function: LPanMenu, TPanMenu function PanMenuSelected(app, event) if app.interactingFlag(event.Source.UserData) == 2 pan(app.figureHandles(event.Source.UserData),'off'); app.interactingFlag(event.Source.UserData) = 0; elseif app.interactingFlag(event.Source.UserData) == 1 zoom(app.figureHandles(event.Source.UserData),'off'); pan(app.figureHandles(event.Source.UserData),'on'); app.interactingFlag(event.Source.UserData) = 2; else pan(app.figureHandles(event.Source.UserData),'on'); app.interactingFlag(event.Source.UserData) = 2; end
end
% Menu selected function: LPointerMenu, TPointerMenu function PointerMenuSelected(app, event) zoom(app.figureHandles(event.Source.UserData),'off'); pan(app.figureHandles(event.Source.UserData),'off'); app.interactingFlag(event.Source.UserData) = 0;
end
% Menu selected function: TResetMenu function TResetMenuSelected(app, event) ylim(app.figureHandles(event.Source.UserData), [app.yLimLowField.Value, app.yLimHighField.Value]); if isempty(app.msConvert*size(app.xi,2)) xlim(app.figureHandles(event.Source.UserData), [0 1]); else xlim(app.figureHandles(event.Source.UserData), [0 app.msConvert*size(app.xi,2)]); end
end
% Menu selected function: LResetMenu function LResetMenuSelected(app, event) ylim(app.figureHandles(event.Source.UserData), [-inf inf]); xlim(app.figureHandles(event.Source.UserData), [-inf inf]);
end
% Menu selected function: TUnselectMenu function TUnselectMenuSelected(app, event) delete(app.pSelection); app.figureHandles(1).UserData.selectedUnassigned = []; if ~isempty(app.pSelected) for ii = 1:length(app.pSelected) if ishandle(app.pSelected(ii)) delete(app.pSelected(ii)); end end end
if ~isempty(app.pSelectedF) for ii = 1:length(app.pSelectedF) if ishandle(app.pSelectedF(ii)) delete(app.pSelectedF(ii)); end end end
app.pSelected = []; app.pSelectedF = []; end
% Menu selected function: LUnselectMenu function LUnselectMenuSelected(app, event) delete(app.lSelection); app.figureHandles(2).UserData{1} = []; if ~isempty(app.pL) for ii = 1:length(app.pL) if ishandle(app.pL(ii)) && strcmp(app.pL(ii).LineStyle, ':') app.pL(ii).LineStyle = '-'; end end end end
% Menu selected function: MatchunityaxisMenu function MatchunityaxisMenuSelected(app, event) % make ylim same on left and right unit plots for ease of comparison
y = ylim(app.figureHandles(2)); y(2,:) = ylim(app.figureHandles(3)); yN(1) = min(y(:,1),[],1); yN(2) = max(y(:,2),[],1); ylim(app.figureHandles(2), yN); ylim(app.figureHandles(3), yN);
end
