% Value changed function: BatchsizeEditField function BatchsizeEditFieldValueChanged(app, event) % change viewing batch size
value = app.BatchsizeEditField.Value;
if value < app.SpikewidthEditField.Value*2 + 1 app.StatusLabel.Value = "Batch size cannot be smaller than twice the spike width!..."; app.BatchsizeEditField.Value = app.t.batchSize; return end
app.t.batchSize = value; bl = app.t.batchLengths;
app.StatusLabel.Value = "Changing batch size..."; drawnow
currentBatchStart = getBatchRange(app); % current batch starting sample currentBatchStart = currentBatchStart(1); batchNum = floor(app.m.fileSizeBytes/(app.m.dbytes*app.m.nChans*app.t.batchSize)); % new number of batches in dataset - 1 app.t.batchLengths = [app.t.batchSize*ones(1, batchNum), app.m.fileSizeBytes/(app.m.dbytes*app.m.nChans)-app.t.batchSize*batchNum];
% plot batch closest in sample number to the old batch blNew = app.t.batchLengths; closestBatch = interp1(cumsum([0 blNew]),1:length(blNew)+1,currentBatchStart,'nearest','extrap'); % offset = app.m.nChans*app.m.dbytes*sum(blNew(1:closestBatch-1)); app.readFilter(closestBatch);
standardUpdate(app);
end
% Value changed function: DetectThr1EditField, % DetectThr2EditField function DetectThrChanged(app, event) % re-detect spikes that are below the new amplitude threshold value = sort([app.DetectThr1EditField.Value, app.DetectThr2EditField.Value]); if value(2) < app.t.detectThr(2) || value(1) > app.t.detectThr(1) % remove prior detected spikes that are now below the threshold % warn user that spikes are about to be deleted msg = 'You are about to change spike detection threshold in a way that can result in deletion of assigned spikes. Continue?'; title = 'Warning - spikes in danger'; selection = uiconfirm(app.UIBase,msg,title,"Icon","warning");
if strcmp(selection, 'OK') app.StatusLabel.Value = "Spike detection amplitude changing..."; drawnow
app.t.detectThr = value; for ii = 1:length(app.unitArray) if ~isempty(app.unitArray(ii).spikeTimes) outBool = app.unitArray(ii).waves(:,ceil(size(app.unitArray(ii).waves,2)/2),... app.unitArray(ii).mainCh) > app.t.detectThr(2); outBool = outBool | app.unitArray(ii).waves(:,ceil(size(app.unitArray(ii).waves,2)/2),... app.unitArray(ii).mainCh) < app.t.detectThr(1); app.unitArray = app.unitArray.spikeRemover(ii,outBool); end end app.readFilter(app.currentBatch); app.unitArray = app.unitArray.unitSorter(); standardUpdate(app); else app.DetectThr1EditField.Value = app.t.detectThr(1); app.DetectThr2EditField.Value = app.t.detectThr(2); end else app.StatusLabel.Value = "Spike detection amplitude changing..."; drawnow
app.t.detectThr = value; app.readFilter(app.currentBatch); standardUpdate(app); end
end
