% Button pushed function: ImporteventmarkerdataButton function ImporteventmarkerdataButtonPushed(app, event) if isfield(app.t,"importedEventFileName") && ~isempty(app.t.importedEventFileName) answer = uiconfirm(app.UIBase,"Event markers have already been loaded in once! Overwrite?","Warning",... 'Icon','warning','DefaultOption',2); if strcmpi(answer, "CANCEL") return; end end
app.StatusLabel.Value = "Loading..."; drawnow
app.switchButtons(1)
[fN, fP] = uigetfile('*.mat','Pick *.mat file to load as event markers'); if fN < 1 app.StatusLabel.Value = "Could not load file!"; return; end app.t.importedEventFileName = [fP, fN];
[app.t.importedEventBounds, app.StatusLabel.Value] = importEventData([fP, fN]); app.VieweventmarkersButton.Enable = 1; app.VieweventmarkersButton.Value = 1; for ii = 1:length(app.pEvent) delete(app.pEvent(ii)); end
standardUpdate(app); end
