% Button pushed function: ImportsampleweightsButton function ImportspikeshapeButtonPushed(app, event) if ~isempty(app.t.deviationSampleWeights) answer = uiconfirm(app.UIBase,"Deviation sample weights have already been loaded in once! Overwrite?","Warning",... 'Icon','warning','DefaultOption',2); if strcmpi(answer, "CANCEL") return; end end
app.StatusLabel.Value = "Loading..."; drawnow
app.switchButtons(1)
[fN, fP] = uigetfile('*.mat','Pick *.mat file to load as deviation sample weights'); if fN < 1 app.StatusLabel.Value = "Could not load file!"; return; end
input = load([fP fN]); vars = fieldnames(input);
if length(vars) < 0 || length(vars) > 1 return; end if min(size(input.(vars{1}))) > 1 return; end if min(input.(vars{1})) < 0 || max(input.(vars{1})) > 1 return; end
if length(input.(vars{1})) < app.m.spikeWidth app.StatusLabel.Value = "The deviation sample weights must be longer than twice the spike width!"; return; end
app.t.deviationSampleWeights = input.(vars{1}); app.ShowsampleweightsButton.Enable = 1;
standardUpdate(app); end
